import{c as r}from"./app.dbf2cbcf.js";import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";var e="/english-note/compare/1.png",o="/english-note/compare/2.png",s="/english-note/compare/3.png",n="/english-note/compare/4.png",t="/english-note/compare/5.png",i="/english-note/compare/6.png",a="/english-note/compare/7.png",g="/english-note/compare/8.png",c="/english-note/compare/9.png",m="/english-note/compare/10.png",_="/english-note/compare/11.png",l="/english-note/compare/12.png",h="/english-note/compare/13.png",v="/english-note/compare/14.png";const d={},f=r('<h1 id="搞事的比较级" tabindex="-1"><a class="header-anchor" href="#搞事的比较级" aria-hidden="true">#</a> 搞事的比较级</h1><p>最近很多人都喜欢上了比较，那我们这一次就用英语来搞事情！</p><img src="'+e+'"><img src="'+o+'"><ol><li>造一个含有形容词的或者副词的句子。</li></ol><img src="'+s+'"><ol start="2"><li>再将句中形容词和副词变成比较级。</li></ol><img src="'+n+'"><ol start="3"><li>再加上比较的对象，想怼谁加谁。</li></ol><img src="'+t+'"><p>比较时，要遵循<strong>去同存异</strong>原则。</p><img src="'+i+'"><img src="'+a+'"><p>简单的搞事都好处理。 按照去同存异的原则， 我们现在去看看 那些想搞个大新闻的同学。 我们搞事的时候就可以看准对象啦！</p><p><strong>搞错对象，可是要负责的！</strong><strong>从属关系间的比较</strong></p><img src="'+g+'"><p>这里比较的是斩护卫的文章，所以，大家不能说，</p><img src="'+c+'"><p>这里的 that 指代 article ， 所以是包大人的文章和斩护卫比。 自己和自己比较</p><img src="'+m+'"><p>跟自己比的时候， 时间不一样， 动词 was 不能省掉哟。</p><p><strong>句子之间的比较</strong></p><img src="'+_+'"><p>一看觉得没毛病， 但是有两层意思。</p><img src="'+l+'"><p>到底是斩护卫爱你 比包大人爱你多， 还是， 斩护卫爱包大人比爱你多呢。 为了区分意思，所以我们只能说，</p><img src="'+h+'"><p>不过有些词是自带搞事光环， perfect，superb，excellent，sublime 这些含义里带有“最”“极其”的单词， 不能有比较级和最高级， 不然真的要上天！</p><p>好了，记住搞事三步曲和去同存异法则， 学会用英文搞事了么？</p><p>我们的口号是！</p><img src="'+v+'">',31);function u(e,p){return f}var N=p(d,[["render",u]]);export{N as default};